mapper.select:
select order_id, order_sn, order_customer_id, order_customer_name, order_customer_phone, order_customer_address, order_money, order_payment_money, order_create_time, order_status, order_modified_time, detail_id, detail_order_id, detail_book_name, detail_book_cnt, detail_book_price, detail_book_totail from order_master om
inner join order_detail od on om.order_id = od.detail_order_id
where om.order_customer_id = #{customerId}

mapper.update:
update order_master
set order_status = #{param2}
where order_id = #{param1}
